DROP DATABASE IF EXISTS forums;

CREATE DATABASE forums;

USE forums;

CREATE TABLE contacts (
  `id` mediumint(8) unsigned NOT NULL auto_increment, 
  `name` varchar(255) default NULL,
  `email` varchar(255) default NULL,
  `city` varchar(50) default NULL,
  `zip` varchar(10) default NULL,
  `country` varchar(50) default NULL,
  PRIMARY KEY (`id`)
) TYPE=InnoDB AUTO_INCREMENT=1; 

CREATE TABLE messages (
  `id` mediumint(8) unsigned NOT NULL auto_increment, 
  `messageText` TEXT default NULL,
  `poster` varchar(50) default NULL,
  `date` varchar(50) default NULL,
  `attachments` varchar(255) default NULL,
  PRIMARY KEY (`id`)
) TYPE=InnoDB AUTO_INCREMENT=1; 


CREATE TABLE threads (
  `id` mediumint(8) unsigned NOT NULL auto_increment, 
  `messageID` MEDIUMINT default NULL,
  `threadID` varchar(50) default NULL,
  `dateCreated` varchar(50) default NULL,
  `threadName` TEXT default NULL,
  PRIMARY KEY (`id`)
) TYPE=Innodb AUTO_INCREMENT=1; 


